<script>
import TopLine from "@/components/common/TopLine.vue";
import NavLine from "@/components/common/NavLine.vue";
import axios from "axios";

export default {
    name: "QueryTrain",
    components: {NavLine, TopLine},
  computed:
      {
        today(){
          const timeElapsed = Date.now();
          const date=new Date(timeElapsed);
          const month=date.getMonth()+1;
          const day=date.getDate();
          return date.getFullYear()+"年"+month+"月"+day+"日";
        }
      },
    data() {
        return {
            departure: null,
            destination: null,
            departTime: null,
            stations:[],
            images: [
              'https://www.12306.cn/index/images/pic/banner20201223.jpg',
              'https://www.12306.cn/index/images/pic/banner12.jpg',
              'https://www.12306.cn/index/images/pic/banner10.jpg',
              'https://www.12306.cn/index/images/pic/banner26.jpg',
                // 'https://zwfw.nra.gov.cn/picture/0/993a7f68b82349d9b9418ad038758420.jpg',
                'https://zwfw.nra.gov.cn/picture/0/4bceefa0a21a40f68e67a1c7e26246a2.jpg',
                'https://zwfw.nra.gov.cn/picture/0/b7db554dd37d49849f0dd9fb517141da.jpg',
                // 'https://zwfw.nra.gov.cn/picture/0/f1bf7d77daf44e9d9544413fb6fc9c48.jpg',
                 

              // 添加更多图片链接...
          ],
          newsimages:[
            'https://www.nra.gov.cn/xwzx/xwxx/xwlb/202305/W020230524417383504687.jpg',
              'https://www.nra.gov.cn/xwzx/xwxx/xwlb/202305/W020230523530628732277.jpg',
              'https://www.nra.gov.cn/xwzx/xwxx/xwlb/202305/W020230515386474785748.png',
              'https://www.gov.cn/yaowen/2023-05/10/W020230511417529778138.jpg'
          ]
        }
    },
    mounted() {
        const jwt = localStorage.getItem('jwt');
        if(jwt){
            this.$store.commit('setJwt', jwt);
        }
        axios.get('api/schedules/stations',
            {
                headers:{'jwt': `${this.jwt}`}
            })
            .then((response)=>
            {
                this.stations=response.data.stations;
            })
            .catch(function(){alert("站点错误！")})
    },
    methods:{
        queryTrip()
        {
            // alert(this.departTime);
            if(this.departTime==null)
            {
              alert("请选择时间！");
              return;
            }
            if(this.departure==null)
            {
              alert("请选择出发地！");
              return;
            }
            if(this.destination==null)
            {
              alert("请选择目的地！");
              return;
            }
            this.$store.dispatch('storeDepartTime',this.departTime);
            this.$store.dispatch('storeDeparture',this.departure);
            this.$store.dispatch('storeDestination',this.destination);
            localStorage.setItem('departTime',this.departTime);
            localStorage.setItem('departure',this.departure);
            localStorage.setItem('destination',this.destination);
            this.$router.push('/querytrain');
        }
    },
}
</script>

